//write a program to calculate power of number using the Math.pow() function

package com.programs.identifiers_operators;

public class MathFunct {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("\n---------Program using Math.pow()---------");
	System.out.println("2^3-->  "+Math.pow(2, 3));
	System.out.println("5^10-->  "+Math.pow(5, 10));
	System.out.println("4^2-->  "+Math.pow(4, 2));
	System.out.println("1^3-->  "+Math.pow(1, 3));
		
	}

}
